     public static boolean updateESByJson(String index, String type, String id, String json) {
         boolean flag = false ;
         try {
              client.prepareUpdate()
                    .setIndex(index)
                    .setType(type)
                    .setId(id)
                    .setDoc(json)
                    .get();
             flag = true ;
         } catch (Exception e) {
             e.printStackTrace();
         }
         return flag;
     }
     
     
